// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title FederatedLearning
 * @dev Manages federated learning training rounds on BNB Chain
 * This contract tracks model training, client participation, and performance metrics
 */
contract FederatedLearning is Ownable, ReentrancyGuard {
    
    struct TrainingRound {
        uint256 roundId;
        uint256 startTimestamp;
        uint256 endTimestamp;
        uint256 numberOfParticipants;
        string globalModelHash; // BNB Greenfield hash of the model
        uint256 accuracy; // scaled by 10000 (4 decimal places)
        uint256 loss; // scaled by 10000 (4 decimal places)
        bool isCompleted;
    }
    
    struct ClientParticipation {
        uint256 clientId;
        uint256 roundId;
        string updateHash; // Client model update hash on BNB Greenfield
        uint256 accuracy; // scaled by 10000 (4 decimal places)
        uint256 loss; // scaled by 10000 (4 decimal places)
        uint256 samples; // Number of data samples used
        uint256 timestamp;
    }
    
    // Storage
    mapping(uint256 => TrainingRound) public trainingRounds;
    mapping(uint256 => mapping(uint256 => ClientParticipation)) public clientParticipations; // roundId => clientId => participation
    mapping(uint256 => uint256) public clientParticipationCount; // roundId => count
    
    uint256 public roundCount;
    uint256 public totalParticipations;
    
    // Events
    event TrainingRoundStarted(
        uint256 indexed roundId,
        uint256 participants,
        uint256 timestamp
    );
    
    event ClientParticipated(
        uint256 indexed roundId,
        uint256 indexed clientId,
        string updateHash,
        uint256 accuracy,
        uint256 loss,
        uint256 samples,
        uint256 timestamp
    );
    
    event TrainingRoundCompleted(
        uint256 indexed roundId,
        string globalModelHash,
        uint256 accuracy,
        uint256 loss,
        uint256 timestamp
    );
    
    // Modifiers
    modifier validRound(uint256 roundId) {
        require(roundId > 0 && roundId <= roundCount, "Invalid round ID");
        _;
    }
    
    modifier onlyTrainer() {
        // In production, implement proper role-based access control
        require(msg.sender == owner(), "Not authorized as trainer");
        _;
    }
    
    /**
     * @dev Record the start of a new training round
     */
    function recordTrainingRoundStart(uint256 participants) external onlyTrainer {
        roundCount++;
        uint256 roundId = roundCount;
        
        TrainingRound storage round = trainingRounds[roundId];
        round.roundId = roundId;
        round.startTimestamp = block.timestamp;
        round.numberOfParticipants = participants;
        round.isCompleted = false;
        
        emit TrainingRoundStarted(roundId, participants, block.timestamp);
    }
    
    /**
     * @dev Record a client's participation in a training round
     */
    function recordClientParticipation(
        uint256 clientId,
        uint256 roundId,
        string calldata updateHash,
        uint256 accuracy,
        uint256 loss,
        uint256 samples
    ) external onlyTrainer validRound(roundId) {
        require(bytes(updateHash).length > 0, "Invalid update hash");
        require(samples > 0, "Must have at least one sample");
        require(!trainingRounds[roundId].isCompleted, "Round already completed");
        
        ClientParticipation storage participation = clientParticipations[roundId][clientId];
        participation.clientId = clientId;
        participation.roundId = roundId;
        participation.updateHash = updateHash;
        participation.accuracy = accuracy;
        participation.loss = loss;
        participation.samples = samples;
        participation.timestamp = block.timestamp;
        
        clientParticipationCount[roundId]++;
        totalParticipations++;
        
        emit ClientParticipated(
            roundId,
            clientId,
            updateHash,
            accuracy,
            loss,
            samples,
            block.timestamp
        );
    }
    
    /**
     * @dev Record the completion of a training round with the global model
     */
    function recordTrainingRoundCompletion(
        uint256 roundId,
        string calldata globalModelHash,
        uint256 accuracy,
        uint256 loss
    ) external onlyTrainer validRound(roundId) {
        TrainingRound storage round = trainingRounds[roundId];
        require(!round.isCompleted, "Round already completed");
        
        round.globalModelHash = globalModelHash;
        round.accuracy = accuracy;
        round.loss = loss;
        round.endTimestamp = block.timestamp;
        round.isCompleted = true;
        
        emit TrainingRoundCompleted(
            roundId,
            globalModelHash,
            accuracy,
            loss,
            block.timestamp
        );
    }
    
    /**
     * @dev Get training round details
     */
    function getTrainingRound(uint256 roundId) external view validRound(roundId) returns (
        uint256 startTimestamp,
        uint256 endTimestamp,
        uint256 numberOfParticipants,
        uint256 actualParticipants,
        string memory globalModelHash,
        uint256 accuracy,
        uint256 loss,
        bool isCompleted
    ) {
        TrainingRound storage round = trainingRounds[roundId];
        return (
            round.startTimestamp,
            round.endTimestamp,
            round.numberOfParticipants,
            clientParticipationCount[roundId],
            round.globalModelHash,
            round.accuracy,
            round.loss,
            round.isCompleted
        );
    }
    
    /**
     * @dev Get client participation details
     */
    function getClientParticipation(uint256 roundId, uint256 clientId) external view validRound(roundId) returns (
        string memory updateHash,
        uint256 accuracy,
        uint256 loss,
        uint256 samples,
        uint256 timestamp
    ) {
        ClientParticipation storage participation = clientParticipations[roundId][clientId];
        require(participation.timestamp > 0, "Client did not participate in this round");
        
        return (
            participation.updateHash,
            participation.accuracy,
            participation.loss,
            participation.samples,
            participation.timestamp
        );
    }
    
    /**
     * @dev Get federated learning statistics
     */
    function getStatistics() external view returns (
        uint256 totalRounds,
        uint256 completedRounds,
        uint256 activeRounds,
        uint256 _totalParticipations,
        uint256 averageParticipantsPerRound
    ) {
        uint256 completed = 0;
        
        for (uint256 i = 1; i <= roundCount; i++) {
            if (trainingRounds[i].isCompleted) {
                completed++;
            }
        }
        
        uint256 avgParticipants = roundCount > 0 ? totalParticipations / roundCount : 0;
        
        return (
            roundCount,
            completed,
            roundCount - completed,
            totalParticipations,
            avgParticipants
        );
    }
}

